import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [targetLanguage, setTargetLanguage] = useState('es');
  const [provider, setProvider] = useState('');
  const [model, setModel] = useState('');
  const [providers, setProviders] = useState([]);
  const [languages, setLanguages] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState(null);
  const [progress, setProgress] = useState(0);
  const [progressMessage, setProgressMessage] = useState('');
  const [translationComplete, setTranslationComplete] = useState(false);
  const [translatedFile, setTranslatedFile] = useState(null);

  useEffect(() => {
    fetchProviders();
    fetchLanguages();
  }, []);

  const fetchProviders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/providers', {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      if (response.data && response.data.providers) {
        setProviders(response.data.providers);
        if (response.data.providers.length > 0) {
          setProvider(response.data.providers[0].name);
          if (response.data.providers[0].models && response.data.providers[0].models.length > 0) {
            setModel(response.data.providers[0].models[0]);
          }
        }
      }
    } catch (error) {
      setError('Failed to fetch providers. Please check if the backend server is running.');
    }
  };

  const fetchLanguages = async () => {
    try {
      const response = await axios.get('http://localhost:5000/supported-languages', {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      if (response.data && response.data.languages) {
        setLanguages(response.data.languages);
        if (response.data.languages.length > 0) {
          setTargetLanguage(response.data.languages[0].code);
        }
      }
    } catch (error) {
      setError('Failed to fetch supported languages.');
    }
  };

  const handleFileSelect = (event) => {
    const selectedFile = event.target.files[0];
    if (selectedFile) {
      setFile(selectedFile);
      setProgress(0);
      setProgressMessage('');
      setTranslationComplete(false);
      setTranslatedFile(null);
      setError(null);
    }
  };

  const handleTranslate = async () => {
    if (!file) {
      setError('Please select a file first');
      return;
    }
    if (!targetLanguage) {
      setError('Please select a target language');
      return;
    }
    if (!provider) {
      setError('Please select a provider');
      return;
    }
    if (!model) {
      setError('Please select a model');
      return;
    }

    setUploading(true);
    setError(null);
    setProgress(0);
    setProgressMessage('Starting translation...');
    setTranslationComplete(false);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('target_language', targetLanguage);
    formData.append('provider', provider);
    formData.append('model', model);

    try {
      const response = await axios.post('http://localhost:5000/translate', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percentCompleted);
          setProgressMessage('Uploading file...');
        },
      });

      if (response.data && response.data.status === 'success') {
        setProgressMessage('Processing translation...');
        const eventSource = new EventSource(
          `http://localhost:5000/translation-progress?id=${response.data.translation_id}`
        );

        eventSource.onmessage = (event) => {
          const data = JSON.parse(event.data);
          if (data.progress) {
            setProgress(data.progress);
          }
          if (data.message) {
            setProgressMessage(data.message);
          }
          if (data.complete) {
            eventSource.close();
            setTranslationComplete(true);
            setTranslatedFile(data.translated_file);
            setUploading(false);
            setProgressMessage('Translation completed!');
          }
          if (data.error) {
            eventSource.close();
            setError(data.error);
            setUploading(false);
          }
        };

        eventSource.onerror = () => {
          eventSource.close();
          setError('Translation failed. Please try again.');
          setUploading(false);
        };
      }
    } catch (error) {
      setError('Failed to upload file. Please try again.');
      setUploading(false);
    }
  };

  const handleDownload = async (filename) => {
    if (!filename) {
      setError('No file available for download');
      return;
    }

    try {
      setError(null);
      const response = await fetch(`http://localhost:5000/download/${encodeURIComponent(filename)}`, {
        method: 'GET',
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to download file');
      }

      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('spreadsheetml')) {
        throw new Error('Invalid file format received');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = filename;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);

    } catch (error) {
      console.error('Download error:', error);
      setError(error.message || 'Failed to download translated file');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <h2 className="text-2xl font-bold mb-8 text-center text-gray-900">Excel Translation App</h2>

                {error && (
                  <div className="bg-red-50 p-4 rounded-md mb-4">
                    <div className="text-sm text-red-700">{error}</div>
                  </div>
                )}

                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Target Language</label>
                  <select
                    value={targetLanguage}
                    onChange={(e) => setTargetLanguage(e.target.value)}
                    className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                  >
                    {languages.map((lang) => (
                      <option key={lang.code} value={lang.code}>
                        {lang.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Provider</label>
                  <select
                    value={provider}
                    onChange={(e) => {
                      setProvider(e.target.value);
                      const selectedProvider = providers.find(p => p.name === e.target.value);
                      if (selectedProvider && selectedProvider.models.length > 0) {
                        setModel(selectedProvider.models[0]);
                      }
                    }}
                    className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                  >
                    <option value="">Select Provider</option>
                    {providers.map((p) => (
                      <option key={p.name} value={p.name}>
                        {p.name}
                      </option>
                    ))}
                  </select>
                </div>

                {provider && (
                  <div className="mb-6">
                    <label className="block text-sm font-medium text-gray-700 mb-2">Model</label>
                    <select
                      value={model}
                      onChange={(e) => setModel(e.target.value)}
                      className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                    >
                      {providers
                        .find((p) => p.name === provider)
                        ?.models.map((m) => (
                          <option key={m} value={m}>
                            {m}
                          </option>
                        ))}
                    </select>
                  </div>
                )}

                <div className="mb-6">
                  <label className="block text-sm font-medium text-gray-700 mb-2">Upload Excel File</label>
                  <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-lg hover:border-indigo-500 transition-colors duration-300">
                    <div className="space-y-1 text-center">
                      <svg
                        className="mx-auto h-12 w-12 text-gray-400"
                        stroke="currentColor"
                        fill="none"
                        viewBox="0 0 48 48"
                        aria-hidden="true"
                      >
                        <path
                          d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                          strokeWidth={2}
                          strokeLinecap="round"
                          strokeLinejoin="round"
                        />
                      </svg>
                      <div className="flex text-sm text-gray-600">
                        <label className="relative cursor-pointer bg-white rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
                          <span>Upload a file</span>
                          <input
                            type="file"
                            className="sr-only"
                            onChange={handleFileSelect}
                            accept=".xlsx,.xls"
                          />
                        </label>
                        <p className="pl-1">or drag and drop</p>
                      </div>
                      <p className="text-xs text-gray-500">Excel files only</p>
                      {file && (
                        <p className="text-sm text-gray-600 mt-2">
                          Selected file: {file.name}
                        </p>
                      )}
                    </div>
                  </div>
                </div>

                {(uploading || progress > 0) && (
                  <div className="mt-6">
                    <div className="relative pt-1">
                      <div className="flex mb-2 items-center justify-between">
                        <div>
                          <span className="text-xs font-semibold inline-block py-1 px-2 uppercase rounded-full text-indigo-600 bg-indigo-200">
                            Progress
                          </span>
                        </div>
                        <div className="text-right">
                          <span className="text-xs font-semibold inline-block text-indigo-600">
                            {progress}%
                          </span>
                        </div>
                      </div>
                      <div className="overflow-hidden h-2 mb-4 text-xs flex rounded bg-indigo-200">
                        <div
                          style={{ width: `${progress}%` }}
                          className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-indigo-500 transition-all duration-300"
                        />
                      </div>
                      {progressMessage && (
                        <p className="text-sm text-gray-600 text-center">
                          {progressMessage}
                        </p>
                      )}
                    </div>
                  </div>
                )}

                <button
                  className={`w-full px-4 py-2 text-white font-bold rounded-lg shadow-md transition-all duration-300 ${
                    !file || uploading
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-indigo-600 hover:bg-indigo-700 hover:shadow-lg'
                  }`}
                  onClick={handleTranslate}
                  disabled={!file || uploading}
                >
                  {uploading ? 'Translating...' : 'Translate'}
                </button>

                {translationComplete && translatedFile && (
                  <div className="mt-6">
                    <button
                      onClick={() => handleDownload(translatedFile)}
                      className="w-full px-4 py-2 text-white font-bold rounded-lg shadow-md bg-green-600 hover:bg-green-700 hover:shadow-lg flex items-center justify-center gap-2"
                    >
                      <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M3 17a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zm3.293-7.707a1 1 0 011.414 0L9 10.586V3a1 1 0 112 0v7.586l1.293-1.293a1 1 0 111.414 1.414l-3 3a1 1 0 01-1.414 0l-3-3a1 1 0 010-1.414z" clipRule="evenodd" />
                      </svg>
                      Download Translated File
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
