import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [targetLanguage, setTargetLanguage] = useState('es');
  const [provider, setProvider] = useState('');
  const [model, setModel] = useState('');
  const [providers, setProviders] = useState([]);
  const [languages, setLanguages] = useState([]);
  const [progress, setProgress] = useState(0);
  const [currentCell, setCurrentCell] = useState('');
  const [isTranslating, setIsTranslating] = useState(false);
  const [error, setError] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [translationComplete, setTranslationComplete] = useState(false);
  const [translatedFile, setTranslatedFile] = useState(null);
  const [progressMessage, setProgressMessage] = useState('');

  useEffect(() => {
    fetchProviders();
    fetchLanguages();
  }, []);

  const fetchProviders = async () => {
    try {
      const response = await axios.get('http://localhost:5000/providers', {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      if (response.data && response.data.providers) {
        setProviders(response.data.providers);
        if (response.data.providers.length > 0) {
          setProvider(response.data.providers[0].name);
          if (response.data.providers[0].models && response.data.providers[0].models.length > 0) {
            setModel(response.data.providers[0].models[0]);
          }
        }
      }
    } catch (error) {
      setError('Failed to fetch providers. Please check if the backend server is running.');
    }
  };

  const fetchLanguages = async () => {
    try {
      const response = await axios.get('http://localhost:5000/supported-languages', {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        }
      });
      if (response.data && response.data.languages) {
        setLanguages(response.data.languages);
        if (response.data.languages.length > 0) {
          setTargetLanguage(response.data.languages[0].code);
        }
      }
    } catch (error) {
      setError('Failed to fetch supported languages.');
    }
  };

  const handleProviderChange = (e) => {
    const selectedProvider = e.target.value;
    setProvider(selectedProvider);
    setModel(''); // Reset model when provider changes
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && !selectedFile.name.match(/\.(xlsx|xls)$/)) {
      setError('Please select an Excel file (.xlsx or .xls)');
      return;
    }
    setFile(selectedFile);
    setError(null);
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    setError(null);
    setProgress(0);
    setTranslationComplete(false);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('target_language', targetLanguage);
    formData.append('provider', provider);
    formData.append('model', model);

    try {
      const response = await axios.post('http://localhost:5000/translate', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          const percentCompleted = Math.round((progressEvent.loaded * 100) / progressEvent.total);
          setProgress(percentCompleted);
        },
      });

      if (response.data && response.data.status === 'success') {
        const eventSource = new EventSource(
          `http://localhost:5000/translation-progress?id=${response.data.translation_id}`
        );

        eventSource.onmessage = (event) => {
          const data = JSON.parse(event.data);
          if (data.progress) {
            setProgress(data.progress);
          }
          if (data.message) {
            setProgressMessage(data.message);
          }
          if (data.complete) {
            eventSource.close();
            setTranslationComplete(true);
            setTranslatedFile(data.translated_file);
          }
          if (data.error) {
            setError(data.error);
            eventSource.close();
          }
        };

        eventSource.onerror = () => {
          eventSource.close();
          setError('Translation failed. Please try again.');
          setUploading(false);
        };
      }
    } catch (error) {
      setError('Failed to upload file. Please try again.');
      setUploading(false);
    }
  };

  const handleTranslate = async () => {
    if (!file || !targetLanguage || !provider || !model) {
      setError('Please fill in all required fields');
      return;
    }

    setIsTranslating(true);
    setProgress(0);
    setError(null);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('target_language', targetLanguage);
    formData.append('provider', provider);
    formData.append('model', model);

    try {
      const eventSource = new EventSource(
        `http://localhost:5000/translate?${new URLSearchParams({
          target_language: targetLanguage,
          provider,
          model,
        })}`
      );

      eventSource.addEventListener('progress', (event) => {
        const data = JSON.parse(event.data);
        setProgress(data.progress);
        setCurrentCell(data.current_cell);
      });

      eventSource.onerror = () => {
        eventSource.close();
        setIsTranslating(false);
        setError('Translation failed. Please try again.');
      };
    } catch (error) {
      setIsTranslating(false);
      setError('Failed to start translation');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <div className="max-w-md mx-auto">
            <div className="divide-y divide-gray-200">
              <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                <h2 className="text-3xl font-bold mb-8 text-center text-indigo-600">Excel Translator</h2>

                {error && (
                  <div className="bg-red-50 border-l-4 border-red-500 p-4 mb-4">
                    <p className="text-red-700">{error}</p>
                  </div>
                )}

                <div className="mb-6">
                  <label className="block text-gray-700 text-sm font-bold mb-2">
                    Target Language
                  </label>
                  <select
                    value={targetLanguage}
                    onChange={(e) => setTargetLanguage(e.target.value)}
                    className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
                  >
                    {languages.map((lang) => (
                      <option key={lang.code} value={lang.code}>
                        {lang.name}
                      </option>
                    ))}
                  </select>
                </div>

                <div className="mb-6">
                  <label className="block text-gray-700 text-sm font-bold mb-2">
                    Select Provider
                  </label>
                  <select
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    value={provider}
                    onChange={handleProviderChange}
                  >
                    <option value="">Select a provider</option>
                    {providers.map((p) => (
                      <option key={p.name} value={p.name}>{p.name.toUpperCase()}</option>
                    ))}
                  </select>
                </div>

                <div className="mb-6">
                  <label className="block text-gray-700 text-sm font-bold mb-2">
                    Select Model
                  </label>
                  <select
                    className="w-full px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:border-indigo-500"
                    value={model}
                    onChange={(e) => setModel(e.target.value)}
                    disabled={!provider}
                  >
                    <option value="">Select a model</option>
                    {provider &&
                      providers.find(p => p.name === provider)?.models.map((m) => (
                        <option key={m} value={m}>{m}</option>
                      ))}
                  </select>
                </div>

                <div className="mb-6">
                  <label className="block text-gray-700 text-sm font-bold mb-2">
                    Upload Excel File
                  </label>
                  <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-lg hover:border-indigo-500 transition-colors duration-300">
                    <div className="space-y-1 text-center">
                      <svg
                        className="mx-auto h-12 w-12 text-gray-400"
                        stroke="currentColor"
                        fill="none"
                        viewBox="0 0 48 48"
                        aria-hidden="true"
                      >
                        <path
                          d="M28 8H12a4 4 0 00-4 4v20m32-12v8m0 0v8a4 4 0 01-4 4H12a4 4 0 01-4-4v-4m32-4l-3.172-3.172a4 4 0 00-5.656 0L28 28M8 32l9.172-9.172a4 4 0 015.656 0L28 28m0 0l4 4m4-24h8m-4-4v8m-12 4h.02"
                          strokeWidth={2}
                          strokeLinecap="round"
                          strokeLinejoin="round"
                        />
                      </svg>
                      <div className="flex text-sm text-gray-600">
                        <label className="relative cursor-pointer bg-white rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500">
                          <span>Upload a file</span>
                          <input
                            type="file"
                            className="sr-only"
                            onChange={handleFileUpload}
                            accept=".xlsx,.xls"
                          />
                        </label>
                        <p className="pl-1">or drag and drop</p>
                      </div>
                      <p className="text-xs text-gray-500">Excel files only</p>
                    </div>
                  </div>
                  {file && (
                    <p className="mt-2 text-sm text-gray-500">
                      Selected file: {file.name}
                    </p>
                  )}
                </div>

                {uploading && (
                  <div className="mt-6">
                    <div className="relative pt-1">
                      <div className="flex mb-2 items-center justify-between">
                        <div>
                          <span className="text-xs font-semibold inline-block py-1 px-2 uppercase rounded-full text-indigo-600 bg-indigo-200">
                            Progress
                          </span>
                        </div>
                        <div className="text-right">
                          <span className="text-xs font-semibold inline-block text-indigo-600">
                            {progress}%
                          </span>
                        </div>
                      </div>
                      <div className="overflow-hidden h-2 mb-4 text-xs flex rounded bg-indigo-200">
                        <div
                          style={{ width: `${progress}%` }}
                          className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-indigo-500 transition-all duration-300"
                        />
                      </div>
                      <p className="text-sm text-gray-600">
                        {progressMessage}
                      </p>
                    </div>
                  </div>
                )}

                {isTranslating && (
                  <div className="mt-6">
                    <div className="relative pt-1">
                      <div className="flex mb-2 items-center justify-between">
                        <div>
                          <span className="text-xs font-semibold inline-block py-1 px-2 uppercase rounded-full text-indigo-600 bg-indigo-200">
                            Progress
                          </span>
                        </div>
                        <div className="text-right">
                          <span className="text-xs font-semibold inline-block text-indigo-600">
                            {progress}%
                          </span>
                        </div>
                      </div>
                      <div className="overflow-hidden h-2 mb-4 text-xs flex rounded bg-indigo-200">
                        <div
                          style={{ width: `${progress}%` }}
                          className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-indigo-500 transition-all duration-300"
                        />
                      </div>
                      <p className="text-sm text-gray-600">
                        Currently processing: {currentCell}
                      </p>
                    </div>
                  </div>
                )}

                <button
                  className={`w-full px-4 py-2 text-white font-bold rounded-lg shadow-md transition-all duration-300 ${
                    uploading || isTranslating
                      ? 'bg-gray-400 cursor-not-allowed'
                      : 'bg-indigo-600 hover:bg-indigo-700 hover:shadow-lg'
                  }`}
                  onClick={handleTranslate}
                  disabled={uploading || isTranslating}
                >
                  {uploading ? 'Uploading...' : isTranslating ? 'Translating...' : 'Translate'}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
